\ Test Memoryanew task-mem_testvariable MT-ORIGIN		\ start address of mem to testvariable MT-COUNT		\ number of bytes to test: MT.SET.AREA  ( addr count -- )	mt-count !	mt-origin !;: MT.PAINT ( n -- )	mt-origin @	mt-count @ 0	DO	2dup c! 1+	LOOP 2drop;: MT.REP.ERROR ( n addr -- )	cr ." Memory error at: " dup .hex ."  , should be " swap . ." , is " c@ .	." Hit key or 'Q':" key ascii Q =	IF abort	THEN;: MT.CHECK ( n --  )	mt-origin @	mt-count @ 0	DO	2dup c@ -		IF 2dup mt.rep.error		THEN		1+	LOOP 2drop;: MT.PAINT+17 ( n -- )	0 mt-origin @	mt-count @ 0	DO	2dup c! 1+ >r 17 + r>	LOOP 2drop;: MT.CHECK+17 ( n -- ok? )	mt-origin @	mt-count @ 0	DO	2dup c@ -		IF 2dup mt.rep.error		THEN		1+ >r 17 + r>	LOOP 2drop;